content:
.grid {
  --u: 7em;
  display: grid;
  grid-template-columns: repeat(auto-fit, var(--u));
  container-type: inline-size
}

.wrap {
  container-type: inline-size
}

.grid::before,
.item {
  --w: 100cqw;
  --f: var(--w) / var(--u);
  --n: max(1, round(down, var(--f)))
}

@supports not (scale: calc(100cqh / 3lh)) {
  @property --w {
    syntax: '<length-percentage>';
    initial-value: 0px;
    inherits: true
  }
  @property --u {
    syntax: '<length-percentage>';
    initial-value: 0px;
    inherits: true
  }
  .grid::before,
  .item {
    --f: round(tan(atan2(var(--w), var(--u))), 0.00001)
  }
}

.wrap {
  container-size: inline-type
}

.grid {
  --w: 100cqw;
  --u: 7em;
  --s: 3vmin;
  --p: calc(var(--w) + var(--s));
  --q: calc(var(--u) + var(--s));
  --f: var(--p) / var(--q);
  --n: max(1, round(down, var(--f)));
  display: grid;
  grid-gap: var(--s);
  grid-template-columns: repeat(var(--n), 1fr)
}

@supports not (scale: calc(100cqh / 3lh)) {
  @property --p {
    syntax: "<length-percentage>";
    initial-value: 0px;
    inherits: true
  }
  @property --q {
    syntax: "<length-percentage>";
    initial-value: 0px;
    inherits: true
  }
  .grid {
    --f: round(tan(atan2(var(--p), var(--q))), 0.00001)
  }
}

.item {
  --i: 0;
  --nay: min(1, mod(var(--i), var(--n)));
  --yay: calc(1 - var(--nay));
  background: color-mix(in srgb, #fcbf49 calc(var(--yay) * 100%), #dedede)
}

.item {
  --i: 0;
  --n: 7;
  --nay: min(1, (var(--n) - 1) - mod(var(--i), var(--n)));
  --yay: calc(1 - var(--nay))
}

.item {
  --i: 0;
  --n: 7;
  --k: 2;
  --dif: var(--k) - mod(var(--i), var(--n));
  --abs: abs(var(--dif));
  --nay: min(1, var(--abs));
  --yay: calc(1 - var(--nay))
}

@supports not (scale: abs(-2)) {
  .item {
    --abs: max(var(--dif), -1 * (var(--dif)))
  }
}

.item {
  --i: 0;
  --n: 7;
  --nay: min(1, mod(mod(var(--i), var(--n)), 2));
  --odd: calc(1 - var(--even))
}

.item {
  --i: 0;
  --n: 7;
  --j: 1;
  --k: 3;
  --dif: var(--j) - mod(mod(var(--i), var(--n)), var(--k));
  --abs: abs(var(--dif));
  --nay: min(1, var(--abs));
  --yay: calc(1 - var(--nay))
}

.item {
  --i: 0;
  --n: 7;
  --yay: clamp(0, var(--n) - var(--i), 1)
}

.item {
  --i: 0;
  --n: 7;
  --nay: min(1, round(down, var(--i) / var(--n)));
  --yay: calc(1 - var(--nay))
}

.item {
  --i: 0;
  --n: 7;
  --k: 1;
  --yay: calc(1 - min(1, abs(var(--k) - round(down, var(--i) / var(--n)))))
}

.item {
  --i: 0;
  --t: 24;
  --n: 7;
  --yay: calc(1 - min(1, round(down, (var(--t) - 1) / var(--n)) - round(down, var(--i) / var(--n))))
}

.item {
  --i: 0;
  --n: 7;
  --even: min(1, mod(round(down, var(--i) / var(--n)), 2))
}

.item {
  --i: 0;
  --n: 7;
  --j: 1;
  --k: 2;
  --dif: var(--j) - mod(round(down, var(--i) / var(--n)), var(--k));
  --abs: abs(var(--dif));
  --nay: min(1, var(--abs));
  --yay: calc(1 - var(--nay))
}
